// fixed position for qaire-sidebar once page scrolls down
$(window).scroll(function(){$(this).scrollTop()>270&&$(".content-sidebar").hasClass("email")?$(".qaire-sidebar").addClass("fixed"):$(this).scrollTop()>100&&!$(".content-sidebar").hasClass("email")?$(".qaire-sidebar").addClass("fixed"):$(".qaire-sidebar").removeClass("fixed")});$(document).on("click",".california-compliance-package .text",function(e){$(e.target).is(".expandable li a")||$(this).parent().find(".checkbox input").click()});